const axios = require('axios');
const FormData = require('form-data');
const fs = require('fs');
const os = require('os');
const path = require('path');
const { cmd, commands } = require('../command');

cmd({
  pattern: 'tourl',
  alias: ['imgtourl', 'imgurl', 'url'],
  react: 'üñá',
  desc: 'Convert media to URL using catbox.moe API.',
  category: 'anime',
  use: '.maid',
  filename: __filename,
}, async (conn, mek, m, { from, quoted, reply }) => {
  try {
    // D√©termine le message contenant le m√©dia (pr√©f√©rence pour le message cit√©)
    let mediaMsg = quoted ? quoted : m;
    let mimetype = (mediaMsg.msg || mediaMsg).mimetype || '';
    if (!mimetype) {
      throw '‚ùå *Error: Please reply to a media message.*';
    }
    
    // T√©l√©charge le m√©dia et sauvegarde-le temporairement
    let fileBuffer = await mediaMsg.download();
    let tempFilePath = path.join(os.tmpdir(), 'catboxupload_' + Date.now());
    fs.writeFileSync(tempFilePath, fileBuffer);
    
    // Pr√©pare les donn√©es du formulaire pour l‚ÄôAPI de catbox.moe
    let form = new FormData();
    form.append('reqtype', 'fileupload');
    form.append('fileToUpload', fs.createReadStream(tempFilePath));
    
    // Effectue la requ√™te POST vers l‚ÄôAPI
    let response = await axios.post('https://catbox.moe/user/api.php', form, {
      headers: { ...form.getHeaders() }
    });
    
    // L'API renvoie l'URL en texte brut en cas de succ√®s
    let urlResult = response.data.trim();
    if (!urlResult.startsWith('http')) {
      throw '‚ùå *Error: Invalid response from catbox.moe API.*';
    }
    
    // Supprime le fichier temporaire
    fs.unlinkSync(tempFilePath);
    
    // Envoie le r√©sultat avec le d√©tail de la taille et les cr√©dits
    reply(
      `*K·¥á Ä·¥ç ·¥ú·¥ò ü·¥è·¥Ä·¥Ö·¥á·¥Ö s·¥ú·¥Ñ·¥Ñ·¥áss“ì·¥ú ü ü è*\n\n` +
      `*Size:* ${fileBuffer.length} Byte(s)\n\n` +
      `*URL:* ${urlResult}\n\n` +
      `> *Uploaded by K·¥á Ä·¥ç MD*`
    );
    
  } catch (error) {
    reply(`‚ùå *An error occurred:*\n${error}`);
    console.error(error);
  }
});